var parent,ink,d,x,y;$(".ripple").mouseup(function(e){ink.addClass("end")});$(".ripple").mousedown(function(e){var item=$(this);parent=item;if(parent.find(".ink").length==0)parent.append("<div class='splash-wrapper'><div class='ink'></div></div>");ink=parent.find(".ink");ink.removeClass("animate");ink.removeClass("end");if(!ink.height()&&!ink.width()){d=Math.max(parent.outerWidth(),parent.outerHeight());ink.css({height:d,width:d})}x=e.pageX-parent.offset().left-ink.width()/2;y=e.pageY-parent.offset().top-ink.height()/2;ink.css({top:y+'px',left:x+'px'}).addClass("animate")});
